import React ,{useLocation, useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';

const Category =()=>{

     const [content,setContent]=useState('');

    const location = useLocation();
    const queryString=URLSearchParams(location.search);

    const onChangeHandler=()=>{
        setContent('');
    };
    const onShowHandler=()=>{
        setContent(<div>
            <Backdrop change={this.onChangeHandler}/>
                <Form/>
            </div>
        );
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
