import React ,{ useEffect, useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

import axios from '../../../assets/axios';

const Category =(props)=>{

     const [content,setContent]=useState('');

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    useEffect(()=>{
        console.log(props.prop);
    },[]);

    const onChangeHandler=()=>{
        setContent(false);
    };

    const getCredHandler=(item)=>{
        console.log(item);
       
        // console.log(queryString.get('categoryName'));
        
        // axios.post('/admin/category',formdata,{
        //     headers:{
        //         'Content-Type':'multipart/form-data',
        //         'Access-Control-Allow-Origin':'*'
        //     }}).then(res=>{
        //         console.log(res);
        //     }).catch(err=>{
        //         console.log(err);
        //     });
        // console.log(formdata);
    };

    const onShowHandler=()=>{
        setContent(true);
           
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content?<div>
                    <Backdrop change={onChangeHandler}/>
                    <Form trigger={getCredHandler}/>
                    </div>:''}
            </div>
    ); 
     
};

export default Category;
