import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

import axios from '../../../assets/axios';

const Category =()=>{

     const [content,setContent]=useState('');
     const [name,setName]=useState('');
     const [status,setStatus]=useState('');
     const [decription,setDesc]=useState('');
     const [category,setCategory]=useState('');

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    useEffect(()=>{
        getCredHandler();
    },[]);

    const onChangeHandler=()=>{
        setContent('');
    };

    const getCredHandler=()=>{
        const status=queryString.get('status');
        const name=queryString.get('name');
        const desc=queryString.get('description');
        const category=queryString.get('categoryName');

        const formdata=new FormData();

        formdata.append('categoryName',name);
        formdata.append('categoryDescription',name);
        formdata.append('categoryName',name);
        formdata.append('categoryName',name);
        console.log(category);
        
    };
    const onShowHandler=()=>{
        
        setContent(<div>
            <Backdrop change={onChangeHandler}/>
                <Form />
            </div>
        );
        
            
        
        
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
