import React ,{Component} from 'react';

import order_logo from '../../images/order_logo.jpg';
import customer_logo from '../../images/customer+logo.jpg';
import product_logo from '../../images/product_logo.jpg';
import category_logo from '../../images/category_logo.jpg';
import './Admin.css';

class Admin extends Component{
    onUploadHnadler=()=>{
        document.getElementById('my-file').click();
    };
    render(){
        return(
            <div>
                <div className="container">
                    <div className="one">
                        <img src={customer_logo}/>
                        Customer
                    </div>
                    <div className="two">
                        <img src={order_logo}/>
                        Order
                    </div>
                    <div className="three">
                        <img src={product_logo}/>
                        Product
                    </div>
                    <div className="four">
                        <img src={category_logo}/>
                        Category
                    </div>
                </div>
                <div className='containerSlider'>
                    <h3>Shop Slider Images</h3>
                    <hr/>
                    <div>
                    <div class="upload-btn-wrapper">
                        <button className="btn">Upload a file</button>
                        <input type="file" name="myfile" />
                    </div>

                    </div>
                </div>
            </div>
        );
    };
};

export default Admin;
