import React ,{ useEffect, useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

const Category =()=>{

     const [content,setContent]=useState('');
     const [name,setName]=useState('');
     const [status,setStatus]=useState('');
     const [decription,setDesc]=useState('');
     const [category,setCategory]=useState('');

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    useEffect(()=>{
        getCredHandler();
    },[]);

    const onChangeHandler=()=>{
        setContent('');
    };

    const getCredHandler=()=>{
        setStatus(queryString.get('status'));
        setName(queryString.get('name'));
        setDesc(queryString.get('description'));
        setStatus(queryString.get('categoryName'));
       
    };
    const onShowHandler=()=>{
        
        setContent(<div>
            <Backdrop change={onChangeHandler}/>
                <Form />
            </div>
        );
        
            
        
        
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
