import React ,{Component} from 'react';
// import {Buffer} from 'node-buffer';

import axio from '../../../assets/axios';
import order_logo from '../../../images/order_logo.jpg';
import customer_logo from '../../../images/customer+logo.jpg';
import product_logo from '../../../images/product_logo.jpg';
import category_logo from '../../../images/category_logo.jpg';
import './Dashboard.css';

class Dashboard extends Component{

    state={
        header:{
            'Content-Type':'multipart/form-data',
            'Access-Control-Allow-Origin':'*'
        },
        path:null
    }

    componentWillMount(){
        axio.get("/admin/sliderImage",this.state).then(res=>{
            console.log(res.data[0].img.data);
            const imgBase64 = res.data[0].img.data.toString("base64");
            console.log(Buffer.from(res.data[0].img.data).toString('base64'));
            console.log(imgBase64);
            // const base64String = btoa(String.fromCharCode(...new Uint8Array(res.data[0].img.data)));
            
            this.setState({path:imgBase64});
        //    this.setState({path:res.data[0].img.data});
        // console.log(base64String);
            
        }).catch(err=>{
            console.log(err);
        });
    }

    onUploadHnadler=(event)=>{

    const fileInfo=event.target.files[0];

    const formdata=new FormData();
    formdata.append('file',fileInfo);
    axio.post("/admin/sliderImage",formdata,this.state).then(res=>{
        console.log(res);
    }).catch(err=>{
        console.log(err);
    });
    
    };
    render(){
        return(
            <div>
            <img src={`data:image/jpeg;base64,${this.state.path}`}  alt="sorry"/>
                <div className="container">
                    <div className="one">
                        <img src={customer_logo}/>
                        Customer
                    </div>
                    <div className="two">
                        <img src={order_logo}/>
                        Order
                    </div>
                    <div className="three">
                        <img src={product_logo}/>
                        Product
                    </div>
                    <div className="four">
                        <img src={category_logo}/>
                        Category
                    </div>
                </div>
                <div className='containerslider'>
                    <h3>Shop Slider Images</h3>
                    <hr/>
                    <div className="upload-btn-wrapper">
                        <button className="btn"> + Upload Images </button>
                        <input type="file" name="myfile" id="file"  onChange={this.onUploadHnadler}/>
                    </div>
                </div>
                <div className='containerslider'>
                    <h3>Today's Orders are #ordercount</h3>
                </div>
            </div>
        );
    };
};

export default Dashboard;
