import React ,{ useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

const Category =()=>{

     const [content,setContent]=useState('');
     const [getParams,setParams]=useState(false);

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    

    const onChangeHandler=()=>{
        setContent('');
    };

    const buttonTriggered=()=>{
        setParams(true);
       
    };
    const onShowHandler=()=>{
        setParams(false);
        setContent(<div>
            <Backdrop change={onChangeHandler}/>
                <Form pressed={buttonTriggered}/>
            </div>
        );
        setParams(true);
        if(getParams){
            console.log(queryString.get('status'));
        }
        
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
