import React ,{ useEffect, useState} from 'react';


import './Category.css';
import Table from '../../Table/Table';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

import axios from '../../../assets/axios';

let cData='';
const Category =(props)=>{

     const [content,setContent]=useState('');
     const [categoryData,setData]=useState(null);

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
    useEffect(()=>{
        axios.get('/admin/category',{
          header:{
              'Content-Type':'multipart/form-data',
              'Access-Control-Allow-Origin':'*'
          }}).then(res=>{
            getDataHandler(res.data);
          }).catch(err=>{
            console.log(err);
          });
    },[]);

    const onChangeHandler=()=>{
        setContent(false);
    };
    
    const getDataHandler=(data)=>{
      cData=data[0];
      
      
    }

    const getCredHandler=(formdata)=>{
        console.log(formdata.get('status'));
        axios.post('/admin/category',formdata,{
            headers:{
                "Content-Type":"multipart/form-data",
                'Access-Control-Allow-Origin':'*'
        }}).then(res=>{
                onChangeHandler();
                console.log(res);
        }).catch(err=>{
                console.log(err);
        });

    };

    const onShowHandler=()=>{
        setContent(true);
           
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                <table >
                <thead>
                  <tr>
                  <th>Image</th>
                  <th>Category Name</th>
                  <th>Category description</th>
                  <th>Category Status</th>
                  <th>#</th>
                  </tr>
                </thead>
                <tbody>
                  {cData}
                </tbody>
              </table>
                {content?<div>
                    <Backdrop change={onChangeHandler}/>
                    <Form trigger={getCredHandler}/>
                    </div>:''}
            </div>
    ); 
     
};

export default Category;
