import React, { useState } from 'react';
import './Form.css';
import Category from '../AdminPanel/Category/Category';
import axios from 'axios';

const Form=(props)=>{
    return(
        <div className='addCategory'>
        <form>
        <h3>Add Category</h3>
        <div className='entries'>
        <label>Category name:</label>
        <input id="name" name='name' type='text'/>
        </div>
        <div className='entries'>
        <label>Category Description:</label>
        <textarea id='description' name='description'/>
        </div>
        <div className='entries'>
        <label>Category Image:</label>
        <input  id='image' name='categoryName' type='file' row='5' onChange={(event)=>{
            console.log(event.target.files[0]);
        }}/>
        </div>
        <div className='entries'>
        <label>Category status:</label>
        <select name="status" id="status">
            <option value='active'>active</option>
            <option value="disable">disable</option>
        </select>
        </div>
        <button onClick={(event)=>{
            const formdata=new FormData();
            formdata.append('name',document.getElementById('name').value);
            formdata.append('description',document.getElementById('description').value);
            formdata.append('category',document.getElementById('category').files[0]);
            formdata.append('status',document.getElementById('status').value);
            console.log(formdata);
            // props.trigger(formdata);
        }} > Save this Category </button>
        </form>
        </div>
       
    );
}

export default Form;
