import React ,{ useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

const Category =()=>{

     const [content,setContent]=useState('');

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    

    const onChangeHandler=()=>{
        setContent('');
    };

    const buttonTriggered=()=>{
        
       
    };
    const onShowHandler=()=>{
        console.log(queryString.get('status'));
            console.log(queryString.get('name'));
            console.log(queryString.get('description'));
            console.log(queryString.get('categoryName'));
        setContent(<div>
            <Backdrop change={onChangeHandler}/>
                <Form pressed={buttonTriggered}/>
            </div>
        );
        
            
        
        
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
