import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

import axios from '../../../assets/axios';

const Category =()=>{

     const [content,setContent]=useState('');
     const [name,setName]=useState('');
     const [status,setStatus]=useState('');
     const [decription,setDesc]=useState('');
     const [category,setCategory]=useState('');

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    useEffect(()=>{
        getCredHandler();
    },[]);

    const onChangeHandler=()=>{
        setContent('');
    };

    const getCredHandler=(formdata)=>{
        axios.post('/admin/ctaegory',formdata,{
            header:{
                'Content-Type':'multipart/form-data',
                'Access-Control-Allow-Origin':'*'
            }}).then(res=>{

            }).catch(err=>{

            });
        console.log(formdata);
    };

    const onShowHandler=()=>{
        setContent(
            <div>
            <Backdrop change={onChangeHandler}/>
           <Form trigger={getCredHandler}/>
            </div>
        );
        
            
        
        
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
