import React ,{ useEffect, useState} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

import axios from '../../../assets/axios';

const Category =()=>{

     const [content,setContent]=useState('');
     const [name,setName]=useState('');
     const [status,setStatus]=useState('');
     const [decription,setDesc]=useState('');
     const [category,setCategory]=useState('');
     const [handler,setHnadler]=useState(false);

     const location = useLocation();
     const queryString= new URLSearchParams(location.search);
     
    useEffect(()=>{
        getCredHandler();
    },[]);

    const onChangeHandler=()=>{
        setContent('');
    };

    const getCredHandler=()=>{
        
        const name=queryString.get('name');
        const file=queryString.get()
        // axios.post('/admin/category',formdata,{
        //     headers:{
        //         'Content-Type':'multipart/form-data',
        //         'Access-Control-Allow-Origin':'*'
        //     }}).then(res=>{
        //         console.log(res);
        //     }).catch(err=>{
        //         console.log(err);
        //     });
        // console.log(formdata);
    };

    const onShowHandler=()=>{
        setContent(
            <div>
            <Backdrop change={onChangeHandler}/>
            <div className='addCategory'>
            <form>
            <h3>Add Category</h3>
            <div className='entries'>
            <label>Category name:</label>
            <input id="name" name='name' type='text'/>
            </div>
            <div className='entries'>
            <label>Category Description:</label>
            <textarea id='description' name='description'/>
            </div>
            <div className='entries'>
            <label>Category Image:</label>
            <input  id='image' name='categoryName' type='file' row='5'/>
            </div>
            <div className='entries'>
            <label>Category status:</label>
            <select name="status" id="status">
                <option value='active'>active</option>
                <option value="disable">disable</option>
            </select>
            </div>
            <button > Save this Category </button>
            </form>
            </div>
            </div>
        );   
    };
    return(
            <div className='container'>
                <button onClick={onShowHandler}> + Add Category</button>
                {content}
            </div>
    ); 
     
};

export default Category;
