import React ,{Component} from 'react';
import './Category.css';
import Backdrop from '../../../UI/Backdrop/Backdrop';
import Form from '../../Forms/addCategory';
import {useLocation} from 'react-router-dom';

class Category extends Component{

    state={
        content:''
    };

    componentDidUpdate(){
        const location = useLocation();
        const queryString=URLSearchParams(location.search);
        console.log(queryString.get('name'));
    }
    onChangeHandler=()=>{
        this.setState({content:''});
    };
    onShowHandler=()=>{
        this.setState({content:<div>
            <Backdrop change={this.onChangeHandler}/>
                <Form/>
            </div>
        });     
    };
    render(){
        return(
            <div className='container'>
                <button onClick={this.onShowHandler}> + Add Category</button>
                {this.state.content}
            </div>
        );
    };  
     
};

export default Category;
